import * as React from 'react';
import {useEffect, useState} from 'react';
import ReactMapGL, {Marker, Popup} from 'react-map-gl';
import {Room, Star} from "@material-ui/icons";
import './app.css'
import axios from "axios";
import {logDOM} from "@testing-library/react";


function App() {
    const [pins, setPins] = useState([])
    const [viewport, setViewport] = useState({
        width: '100vw',
        height: '100vh',
        latitude: 46,
        longitude: 17,
        zoom: 4
    });
    useEffect(() => {
        const getPins = async () => {
            try {
                const res = await axios.get('/pins')
                setPins(res.data)
            } catch (err) {
                console.log(err, 'err in useEffect getting pins')
            }
        }
        getPins().then(r => console.log(r,'this is R'))
    }, [])
    return (
        <div className="App">
            <ReactMapGL
                {...viewport} //מחזיק את כל המאפיינים
                mapboxApiAccessToken={process.env.REACT_APP_MAPBOX}
                onViewportChange={nextViewport => setViewport(nextViewport)}
                mapStyle="mapbox://styles/safak/cknndpyfq268f17p53nmpwira"
            >
                {pins.map(pin => (
                    <>
                        <Marker latitude={pin.lat} longitude={pin.long} offsetLeft={-20} offsetTop={-10}>
                            <Room style={{fontSize: viewport.zoom * 7, color: 'red'}}/>
                        </Marker>
                        <Popup
                            latitude={pin.lat}
                            longitude={pin.long}
                            closeButton={true}
                            closeOnClick={false}
                            anchor="top">
                            <div className={'card'}>
                                <label>Place</label>
                                <h4 className={'place'}>{pin.title}</h4>
                                <label>Review</label>
                                <p className={'desc'}>{pin.desc}</p>
                                <label>Rating</label>
                                <div className={'stars'}>
                                    <Star className={'star'}/>
                                    <Star className={'star'}/>
                                    <Star className={'star'}/>
                                    <Star className={'star'}/>
                                    <Star className={'star'}/>
                                </div>
                                <label>Info</label>
                                <span className={'username'}>Created By <b>{pin.username}</b></span>
                                <span className={'date'}>1 hour ago</span>
                            </div>
                        </Popup>
                    </>
                ))}

            </ReactMapGL>
        </div>
    );
}

export default App;
